```js
function parseQueryString(url){
  if(typeof url !== 'string') throw new Error('invalid url')
  const search = decodeURIComponent(url).split('?')[1];
  if(!search) return {};
  return search.split('&').reduce((pre, cur) => {
    const [key, value] = cur.split('=');
    pre[key] = value;
    return pre;
  }, {});
}
